package offer;

import org.junit.jupiter.api.Test;

/**
 * @author Achan
 * @date 2020/10/3
 */
public class SwordFinger50 {
    public char firstUniqChar(String s) {
        char[] chars = s.toCharArray();
        int[] map = new int['z' - 'a' + 1];
        for (char c : chars) {
            map[c - 'a']++;
        }
        for (char c : chars) {
            if (map[c - 'a'] == 1) {
                return c;
            }
        }
        return ' ';
    }

    @Test
    void firstUniqCharTest() {
        System.out.println(firstUniqChar(""));
    }
}
